const now = new Date();
const reservationDateTime = new Date(`${input.reservation_date}T${input.reservation_time}`);
const hoursUntil = (reservationDateTime - now) / (1000 * 60 * 60);
const minutesUntil = (reservationDateTime - now) / (1000 * 60);

console.log('â° Tiempo hasta reserva:', {
  hours: hoursUntil.toFixed(2),
  minutes: minutesUntil.toFixed(0),
  reservation_datetime: reservationDateTime.toISOString()
});

// Determinar tipo de mensaje
let messageType = 'immediate';
let sendNow = false;
let reason = '';

if (hoursUntil > 24) {
  messageType = '24h';
  sendNow = false;
  reason = 'MÃ¡s de 24h: Dejar que cron 24h lo maneje';
} else if (hoursUntil >= 4) {
  messageType = '4h';
  sendNow = true;
  reason = 'Entre 4-24h: Enviar confirmaciÃ³n AHORA';
} else if (hoursUntil >= 2) {
  messageType = '2h';
  sendNow = true;
