} else {
  throw new Error('âŒ La reserva ya pasÃ³ (fecha/hora en el pasado)');
}

console.log('âœ… DecisiÃ³n:', { messageType, sendNow, reason });

return {
  ...input,
  hours_until: hoursUntil,
  minutes_until: minutesUntil,
  message_type: messageType,
  send_now: sendNow,
  decision_reason: reason
};

