console.log('   TO (Cliente):', whatsappTo);

// Validar que los telÃ©fonos existan
if (!whatsappFrom) {
  console.error('âŒ No se encontrÃ³ nÃºmero FROM. Verifica channels.voice.phone_number');
}

if (!whatsappTo) {
  console.error('âŒ No se encontrÃ³ nÃºmero TO (cliente)');
}

return {
  conversation_id: contextData.conversation_id,
  customer_id: contextData.customer_id,
  customer_phone: whatsappTo,
  customer_name: contextData.customer_name,
  restaurant_id: contextData.restaurant_id,
  channel: contextData.channel,
  agent_response: finalMessage,
  classification: contextData.classification,
  timestamp: new Date().toISOString(),

  // âœ… Para envÃ­o de WhatsApp (CORREGIDO)
  whatsapp_from: whatsappFrom,  // channels.voice.phone_number
  whatsapp_to: whatsappTo        // customer_phone
};

// Parsear clasificaciÃ³n del LLM
const inputData = $('ğŸ“‹ Preparar Input').item.json;
const llmResponse = $input.item.json.response || $input.item.json.text || '{}';

// Limpiar markdown si lo hay
let cleanedResponse = llmResponse.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

// Parsear JSON
let classification;
try {
  classification = JSON.parse(cleanedResponse);
  console.log('âœ… ClasificaciÃ³n exitosa:', classification);
} catch (e) {
  console.error('âŒ Error parseando clasificaciÃ³n:', e);
  console.error('Respuesta recibida:', cleanedResponse);
  classification = {
    intent: 'consulta_general',
    entities: {},
    sentiment: 'neutral',
    confidence: 0.5,
    reasoning: 'Error en clasificaciÃ³n, usando default'
  };
}

return {
  ...inputData,
  classification: classification,
  intent: classification.intent,
  entities: classification.entities,
  sentiment: classification.sentiment,
  confidence: classification.confidence
};

// =====================================
// NODO: ğŸ”€ Preparar ActualizaciÃ³n
// TIPO: Code
// POSICIÃ“N: ANTES de "ğŸ”„ Actualizar ConversaciÃ³n"
// =====================================

const data = $input.first().json;

// Mapeo profesional de intents espaÃ±ol â†’ inglÃ©s (valores vÃ¡lidos en BD)
const intentMap = {
  'reservar': 'reservation',
  'modificar': 'modification',
  'modificaciÃ³n': 'modification',
  'cancelar': 'cancellation',
  'cancelaciÃ³n': 'cancellation',
  'consultar': 'inquiry',
  'consulta': 'inquiry',
  'queja': 'complaint',
  'quejas': 'complaint',
  'feedback': 'feedback',         // âœ¨ NUEVO
  'opiniÃ³n': 'feedback',          // âœ¨ NUEVO
  'valoraciÃ³n': 'feedback',       // âœ¨ NUEVO
  'satisfacciÃ³n': 'feedback',     // âœ¨ NUEVO
  'informaciÃ³n': 'inquiry',
  'info': 'inquiry',
  'otro': 'other'
};

// Obtener el intent de la clasificaciÃ³n
const intent = data.classification?.intent || 'consultar';
const interactionType = intentMap[intent.toLowerCase()] || 'inquiry';

// Preparar metadata completa y estructurada
const metadata = {
  classification: {
    intent: data.classification.intent,
    entities: data.classification.entities || {},
    sentiment: data.classification.sentiment || 'neutral',
