const input = $input.first().json;

console.log('🔍🔍🔍 DEBUG CRÍTICO - INPUT TOOL-6:');
console.log('   conversation_id:', input.conversation_id);
console.log('   customer_phone:', input.customer_phone);
console.log('   restaurant_id:', input.restaurant_id);
console.log('   timestamp:', input.timestamp || 'NO TIMESTAMP');

if (!input.conversation_id) {
  throw new Error('❌ CRÍTICO: No hay conversation_id');
}

if (!input.customer_phone) {
  throw new Error('❌ CRÍTICO: No hay customer_phone');
}

return input;

const input = $('▶️ Start (Tool Call)').first().json;
const supabaseResult = $input.first().json;

console.log('🆘 ESCALADO A HUMANO:', input);
console.log('🔍 Resultado Supabase RAW:', JSON.stringify(supabaseResult, null, 2));

const reason = input.reason || 'cliente_solicita';
const customerPhone = input.customer_phone || 'No disponible';
const customerName = input.customer_name || 'Cliente';

// ✅ BUSCAR EL MENSAJE - ORDEN DE PRIORIDAD
let customerMessage = null;
let source = 'default';

// 🔍 PRIORIDAD 1: Resultado de SQL query (array en data)
if (supabaseResult && Array.isArray(supabaseResult) && supabaseResult.length > 0) {
  customerMessage = supabaseResult[0].message_text;
  source = 'sql-array-root';
  console.log('✅ Opción 1 - SQL array root:', customerMessage?.substring(0, 50));
}
// 🔍 PRIORIDAD 2: Resultado en data array
else if (supabaseResult && Array.isArray(supabaseResult.data) && supabaseResult.data.length > 0) {
  customerMessage = supabaseResult.data[0].message_text;
  source = 'sql-array-data';
  console.log('✅ Opción 2 - SQL array data:', customerMessage?.substring(0, 50));
}
// 🔍 PRIORIDAD 3: Directamente en supabaseResult
else if (supabaseResult && supabaseResult.message_text) {
  customerMessage = supabaseResult.message_text;
  source = 'direct';
  console.log('✅ Opción 3 - Direct:', customerMessage?.substring(0, 50));
}
// 🔍 PRIORIDAD 4: En data object
else if (supabaseResult && supabaseResult.data && supabaseResult.data.message_text) {
  customerMessage = supabaseResult.data.message_text;
  source = 'data-object';
  console.log('✅ Opción 4 - Data object:', customerMessage?.substring(0, 50));
}
// 🔍 PRIORIDAD 5: user_message del input (SIEMPRE debe existir)
else if (input.user_message) {
  customerMessage = input.user_message;
  source = 'input-fallback';
  console.log('✅ Opción 5 - Input fallback:', customerMessage?.substring(0, 50));
}

// 🔴 Si NINGUNA opción funcionó, usar default
if (!customerMessage) {
  customerMessage = 'Cliente solicitó hablar con el encargado';
  source = 'default';
  console.log('⚠️ NO SE ENCONTRÓ MENSAJE - usando default');
  console.log('⚠️ Input keys:', Object.keys(input));
  console.log('⚠️ Supabase keys:', Object.keys(supabaseResult || {}));
}

console.log('📊 Source usado:', source);

// Si el mensaje es muy largo (>200 caracteres), hacer resumen
if (customerMessage && customerMessage.length > 200) {
  customerMessage = customerMessage.substring(0, 197) + '...';
}

const urgency = input.urgency || 'high';

// Mapeo de razones a español
const reasonMap = {
  'cliente_solicita': '🙋 El cliente solicita hablar con una persona',
  'situacion_compleja': '🤔 Situación compleja fuera del alcance del agente',
  'queja_grave': '😡 Queja grave del cliente',
  'informacion_no_disponible': 'ℹ️ Información no disponible en el sistema',
  'error_sistema': '⚠️ Error del sistema'
};

const reasonText = reasonMap[reason] || reason;
const urgencyEmoji = urgency === 'high' ? '🚨🚨🚨' : '⚠️';

console.log('📤 MENSAJE FINAL:', customerMessage);
console.log('📤 Source:', source);

return {
  reason,
  reasonText,
  customerPhone,
  customerName,
  customerMessage,
  urgency,
  urgencyEmoji,
  timestamp: new Date().toISOString(),
  _debug_source: source
};

const escalation = $('📋 Preparar Escalado').first().json;
const restaurant = $input.first().json;

const channels = restaurant.channels || {};

// ✅ Teléfonos correctos
const twilioNumber = channels.voice?.phone_number || '+14155238886';
const emergencyPhone = channels.whatsapp?.emergency_phone || restaurant.phone;

// Validaciones
if (!twilioNumber) {
  throw new Error('❌ No se encontró número de canales IA');
}

if (!emergencyPhone) {
  throw new Error('❌ No se encontró teléfono de emergencia');
}

// ✅ Hora en zona horaria de España (Europe/Madrid)
const horaEspana = new Date().toLocaleString('es-ES', {
  timeZone: 'Europe/Madrid',
  day: '2-digit',
  month: '2-digit',
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit'
});

// Construir mensaje de alerta
const message = `${escalation.urgencyEmoji} ALERTA - CLIENTE NECESITA ATENCIÓN\n\n${escalation.reasonText}\n\n👤 Cliente: ${escalation.customerName}\n📞 Teléfono: ${escalation.customerPhone}\n⏰ Hora: ${horaEspana}\n\n💬 Mensaje del cliente:\n"${escalation.customerMessage}"\n\n⚡ Acción requerida: Contactar al cliente URGENTEMENTE\n\n---\nRestaurante: ${restaurant.name}`;

console.log('📤 Mensaje completo:', message);

return {
  ...escalation,
  restaurant_name: restaurant.name,
  twilio_number: twilioNumber,
  emergency_phone: emergencyPhone,
  alert_message: message
};

const escalation = $('📋 Preparar Escalado').first().json;

console.log('✅ Escalado completado:', escalation.customerName);

return {
  success: true,
  message: 'El encargado ha sido notificado y te contactará en breve.',
  escalated: true,
  escalation_id: $input.first().json.id
};
