// =====================================================
// ğŸ” VALIDACIÃ“N DE INPUT CON VALIDACIÃ“N DE ZONAS
// =====================================================

const input = $input.first().json;
const restaurant = $('ğŸª Obtener Restaurant Settings').first().json;

console.log('ğŸ“¥ Input recibido:', input);

// ===== VALIDAR FECHA =====
const fecha = input.date || input.reservation_date || '';

if (!fecha || !/^\d{4}-\d{2}-\d{2}$/.test(fecha)) {
  throw new Error('Formato de fecha invÃ¡lido. Se requiere YYYY-MM-DD');
}

const hoy = new Date().toISOString().split('T')[0];
if (fecha < hoy) {
  throw new Error('No se puede consultar disponibilidad en fechas pasadas');
}

// ===== VALIDAR HORA =====
const hora = input.time || input.reservation_time || '';

if (!hora || !/^\d{2}:\d{2}(:\d{2})?$/.test(hora)) {
  throw new Error('Formato de hora invÃ¡lido. Se requiere HH:MM');
}

const horaNormalizada = hora.substring(0, 5);

// ===== VALIDAR PERSONAS =====
const personas = parseInt(input.party_size || input.people || 0);

if (!personas || personas < 1 || personas > 20) {
  throw new Error('NÃºmero de personas debe estar entre 1 y 20');
}

// ===== VALIDAR RESTAURANT_ID =====
const restaurant_id = input.restaurant_id || '';

if (!restaurant_id) {
  throw new Error('restaurant_id es obligatorio');
}

// âœ…âœ…âœ… NUEVA VALIDACIÃ“N DE ZONAS
const zonesConfig = restaurant.settings?.zones || {};
const availableZones = [];

// Extraer zonas activas
Object.entries(zonesConfig).forEach(([zoneKey, zoneData]) => {
  if (zoneData && zoneData.enabled === true) {
    availableZones.push(zoneKey);
  }
});

console.log('ğŸ—ºï¸ Zonas activas del restaurante:', availableZones);

// âœ… VALIDAR Y NORMALIZAR ZONA
const zonaRaw = input.preferred_zone || input.zone || input.reservation_location || input.location || null;
let zona = null;
let zonaValidada = false;

if (zonaRaw) {
  const zonaNormalizada = zonaRaw.toLowerCase().trim();

  // Si es "any", buscar en todas las zonas activas
  if (zonaNormalizada === 'any') {
    zona = null;
    zonaValidada = true;
    console.log('âœ… Zona: "any" - Se buscarÃ¡ en todas las zonas activas');
  } 
  // Validar que la zona estÃ© en las zonas activas
  else if (availableZones.includes(zonaNormalizada)) {
    zona = zonaNormalizada;
    zonaValidada = true;
    console.log(`âœ… Zona vÃ¡lida y activa: ${zona}`);
  } 
  // Zona no estÃ¡ activa o no existe
  else {
    console.log(`âš ï¸ Zona "${zonaRaw}" no estÃ¡ activa o no existe. Zonas disponibles: ${availableZones.join(', ')}`);
    // Buscar en todas las zonas activas como fallback
    zona = null;
    zonaValidada = true;
    console.log('â„¹ï¸ Buscando en todas las zonas activas como alternativa');
  }
} else {
  // Si no especificÃ³ zona, buscar en todas las activas
  zona = null;
  zonaValidada = true;
  console.log('â„¹ï¸ No se especificÃ³ zona, se buscarÃ¡ en todas las zonas activas');
}

console.log('âœ… Validado:', { fecha, hora: horaNormalizada, personas, zona: zona || 'todas las activas', restaurant_id });

return {
  fecha,
  hora: horaNormalizada,
  personas,
  zona,
  zona_solicitada_original: zonaRaw,
  zonas_disponibles: availableZones,
  restaurant_id,
  fecha_formateada: new Date(fecha).toLocaleDateString('es-ES', { 
    weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' 
  })
};

// =====================================================
// âœ… RESPUESTA: SÃ HAY DISPONIBILIDAD (CON ZONAS)
// =====================================================

const validacion = $('ğŸ” Validar Input + Zonas').first().json;
const slots = $input.all();

const personas = validacion.personas;
const zona = validacion.zona;
const zonasDisponibles = validacion.zonas_disponibles;

console.log('âœ… Slots disponibles:', slots.length);

if (slots.length === 0) {
  const mensajeBase = `Lo siento, no tenemos mesas con capacidad suficiente para ${personas} personas`;
  const mensajeZona = zona ? ` en la zona ${zona}` : '';
  const sugerenciaZonas = zonasDisponibles.length > 1 ? ` Zonas disponibles: ${zonasDisponibles.join(', ')}. Â¿Te irÃ­a bien otra zona?` : '';

  return {
    disponible: false,
    mensaje: `${mensajeBase}${mensajeZona}.${sugerenciaZonas}`,
    alternativas: [],
    detalles: {
      fecha: validacion.fecha,
      hora: validacion.hora,
      personas: personas,
      zona_solicitada: zona,
      zonas_disponibles: zonasDisponibles
    },
    accion_sugerida: zonasDisponibles.length > 1 ? 'sugerir_otra_zona' : 'buscar_otra_hora'
  };
}

const mesasDisponibles = slots
  .map(s => s.json)
  .sort((a, b) => a.capacity - b.capacity);

const mejorMesa = mesasDisponibles[0];

const mensajeZona = zona ? ` en ${zona}` : '';
const mensaje = `Â¡Perfecto! SÃ­ tenemos disponibilidad${mensajeZona} para ${personas} persona${personas > 1 ? 's' : ''} el ${validacion.fecha_formateada} a las ${validacion.hora}. Tenemos ${mesasDisponibles.length} mesa${mesasDisponibles.length > 1 ? 's' : ''} disponible${mesasDisponibles.length > 1 ? 's' : ''}.`;

return {
  disponible: true,
  mensaje: mensaje,
  detalles: {
    fecha: validacion.fecha,
    hora: validacion.hora,
    personas: personas,
    zona_solicitada: zona,
    zonas_disponibles: zonasDisponibles,
    mesas_disponibles: mesasDisponibles.length,
    mejor_opcion: {
      mesa: mejorMesa.table_name,
      capacidad: mejorMesa.capacity,
      zona: mejorMesa.zone
    },
    todas_las_opciones: mesasDisponibles.map(m => ({
      mesa: m.table_name,
      capacidad: m.capacity,
      zona: m.zone
    }))
  },
  accion_sugerida: 'proceder_con_reserva'
};

// =====================================================
// âš ï¸ RESPUESTA: NO HAY DISPONIBILIDAD (CON ZONAS)
// =====================================================

const validacion = $('ğŸ” Validar Input + Zonas').first().json;
const alternativas = $input.all();

const zona = validacion.zona;
const personas = validacion.personas;
const zonasDisponibles = validacion.zonas_disponibles;

console.log('ğŸ” Alternativas encontradas:', alternativas.length);
console.log('ğŸ—ºï¸ Zonas disponibles del restaurante:', zonasDisponibles);

// ===== CASO 1: NO HAY ALTERNATIVAS EN TODO EL DÃA =====
if (alternativas.length === 0) {
  return {
    tiene_alternativas: false,
    mensaje: `Lo siento, no tenemos disponibilidad para ${personas} personas el ${validacion.fecha_formateada}. Â¿Te gustarÃ­a que busque en otra fecha?`,
    disponible: false,
    alternativas: [],
    detalles: {
      fecha_solicitada: validacion.fecha,
      hora_solicitada: validacion.hora,
      personas: personas,
      zona_solicitada: zona,
      zonas_disponibles: zonasDisponibles
    },
    accion_sugerida: 'sugerir_otra_fecha'
  };
}

// ===== CASO 2: HAY SLOTS EN OTRAS ZONAS ACTIVAS =====
if (zona) {
  // Filtrar solo las zonas activas
  const zonasAlternativas = [...new Set(
    alternativas
      .map(s => s.json.zone)
      .filter(z => z && z !== zona && zonasDisponibles.includes(z))
  )];

  if (zonasAlternativas.length > 0) {
    const listaZonas = zonasAlternativas.join(', ');
    return {
      tiene_alternativas: true,
      disponible: false,
      mensaje: `Lo siento, no tenemos disponibilidad en ${zona} a las ${validacion.hora} para ${personas} personas. Sin embargo, sÃ­ tenemos disponibilidad en: ${listaZonas}. Â¿Te irÃ­a bien alguna de estas zonas?`,
      alternativas_zona: zonasAlternativas,
      detalles: {
        fecha: validacion.fecha,
        hora_solicitada: validacion.hora,
        personas: personas,
        zona_solicitada: zona,
        zonas_alternativas: zonasAlternativas,
        todas_zonas_disponibles: zonasDisponibles
      },
      accion_sugerida: 'elegir_otra_zona'
    };
  }
}

// ===== CASO 3: HAY SLOTS EN OTRAS HORAS (SOLO ZONAS ACTIVAS) =====
const horasPorSlot = {};
alternativas.forEach(slot => {
  const hora = slot.json.start_time;
  const zonaSlot = slot.json.zone;

  // Solo considerar zonas activas
  if (!zonasDisponibles.includes(zonaSlot)) {
    return;
  }

  // Si especificÃ³ zona, solo esa zona
  if (zona && zonaSlot !== zona) {
    return;
  }

  if (!horasPorSlot[hora]) {
    horasPorSlot[hora] = {
      slots: 0,
      capacity_total: 0
    };
  }
  horasPorSlot[hora].slots++;
  horasPorSlot[hora].capacity_total += slot.json.capacity;
});

const horasDisponibles = Object.keys(horasPorSlot)
  .filter(h => h !== validacion.hora)
  .sort()
  .slice(0, 5);

if (horasDisponibles.length === 0) {
  const mensajeZona = zona ? ` en ${zona}` : '';
  const sugerenciaZonas = !zona && zonasDisponibles.length > 1 ? ` (Zonas disponibles: ${zonasDisponibles.join(', ')})` : '';
  return {
    tiene_alternativas: false,
    disponible: false,
    mensaje: `Lo siento, no tenemos disponibilidad${mensajeZona} para ${personas} personas el ${validacion.fecha_formateada}${sugerenciaZonas}. Â¿Te gustarÃ­a que busque en otra fecha?`,
    alternativas: [],
    detalles: {
      fecha: validacion.fecha,
      hora_solicitada: validacion.hora,
      personas: personas,
      zona_solicitada: zona,
      zonas_disponibles: zonasDisponibles
    },
    accion_sugerida: !zona && zonasDisponibles.length > 1 ? 'elegir_zona_primero' : 'sugerir_otra_fecha'
  };
}

const listaHoras = horasDisponibles.join(', ');
const mensajeZona = zona ? ` en ${zona}` : '';
const mensaje = `Lo siento, no tenemos disponibilidad a las ${validacion.hora}${mensajeZona} para ${personas} personas. Sin embargo, sÃ­ tenemos disponibilidad en estos otros horarios el ${validacion.fecha_formateada}: ${listaHoras}. Â¿Alguno de estos horarios te vendrÃ­a bien?`;

return {
  tiene_alternativas: true,
  disponible: false,
  mensaje: mensaje,
  alternativas_horario: horasDisponibles,
  detalles: {
    fecha: validacion.fecha,
    hora_solicitada: validacion.hora,
    personas: personas,
    zona_solicitada: zona,
    zonas_disponibles: zonasDisponibles,
    total_alternativas: horasDisponibles.length
  },
  accion_sugerida: 'elegir_alternativa'
};
