const input = $input.item.json;

console.log('ğŸ” Input recibido:', JSON.stringify(input, null, 2));

// âœ… Validar datos mÃ­nimos
const required = ['reservation_date', 'reservation_time', 'party_size', 'restaurant_id'];
const missing = required.filter(field => !input[field]);

if (missing.length > 0) {
  throw new Error(`âŒ Faltan campos obligatorios: ${missing.join(', ')}`);
}

return {
  // âœ… DATOS OBLIGATORIOS
  restaurant_id: input.restaurant_id,
  customer_id: input.customer_id,  // âœ… YA VIENE DEL GATEWAY
  customer_phone: input.customer_phone,  // âœ… YA VIENE DEL GATEWAY
  customer_name: input.customer_name,  // âœ… YA VIENE DEL GATEWAY

  // âœ… DATOS DE LA RESERVA
  reservation_date: input.reservation_date,  // 'YYYY-MM-DD'
  reservation_time: input.reservation_time,  // 'HH:MM'
  party_size: parseInt(input.party_size),

  // âœ… DATOS OPCIONALES
  preferred_zone: input.preferred_zone || null,
  special_requests: input.special_requests || null,
  source: input.source || 'agent_whatsapp'  // âœ… Fuente de la reserva
};

const slots = $input.all();
const validacion = $('ğŸ” Validar Input').first().json;

console.log('ğŸ“Š Slots encontrados:', slots.length);

if (slots.length === 0) {
  return {
    disponible: false,
    mensaje: `No hay mesas disponibles para ${validacion.party_size} personas el ${validacion.reservation_date} a las ${validacion.reservation_time}.`
  };
}

// âœ… FILTRAR POR CAPACIDAD (ahora capacity estÃ¡ en availability_slots)
const slotsWithCapacity = slots.filter(s => {
  const capacity = s.json.capacity;
  return capacity >= validacion.party_size;
});

console.log('âœ… Slots con capacidad suficiente:', slotsWithCapacity.length);

if (slotsWithCapacity.length === 0) {
  return {
    disponible: false,
    mensaje: `No hay mesas con capacidad para ${validacion.party_size} personas en esa fecha y hora.`
  };
}

// âœ… Ordenar por capacidad ASC (la mÃ¡s pequeÃ±a que cumpla)
const sorted = slotsWithCapacity.sort((a, b) => a.json.capacity - b.json.capacity);
const mejorSlot = sorted[0].json;

console.log('ğŸ¯ Mejor slot seleccionado:', {
  id: mejorSlot.id,
  table_id: mejorSlot.table_id,
  table_name: mejorSlot.table_name,
  capacity: mejorSlot.capacity,
  zone: mejorSlot.zone
});

return {
  disponible: true,
  slot_id: mejorSlot.id,
  table_id: mejorSlot.table_id,
  table_name: mejorSlot.table_name,
  capacity: mejorSlot.capacity,
  zone: mejorSlot.zone,
  start_time: mejorSlot.start_time,
  end_time: mejorSlot.end_time,
  reservation_date: validacion.reservation_date,
  reservation_time: validacion.reservation_time,
  party_size: validacion.party_size,
  restaurant_id: validacion.restaurant_id,
  customer_id: validacion.customer_id,
  customer_phone: validacion.customer_phone,
  customer_name: validacion.customer_name,
  special_requests: validacion.special_requests
};

const reservation = $('ğŸ’¾ Crear Reserva').first().json;
const slotData = $('ğŸ¯ Seleccionar Mejor Mesa').first().json;

console.log('âœ… Reserva creada:', reservation.id);
console.log('âœ… Slot marcado como reservado:', slotData.slot_id);

return {
  success: true,
  reservation_id: reservation.id,
  message: `Â¡Listo, ${reservation.customer_name}! Tu reserva estÃ¡ confirmada para el ${reservation.reservation_date} a las ${reservation.reservation_time} para ${reservation.party_size} personas. Mesa: ${slotData.table_name} (${slotData.zone}).`,
  details: {
    reservation_id: reservation.id,
    date: reservation.reservation_date,
    time: reservation.reservation_time,
    party_size: reservation.party_size,
    table: slotData.table_name,
    zone: slotData.zone,
    customer_name: reservation.customer_name,
    special_requests: reservation.special_requests || 'Ninguna'
  }
};

const data = $input.first().json;

console.log('âŒ No hay disponibilidad:', data.mensaje);

return {
  success: false,
  message: data.mensaje,
  details: {
    date: $('ğŸ” Validar Input').first().json.reservation_date,
    time: $('ğŸ” Validar Input').first().json.reservation_time,
    party_size: $('ğŸ” Validar Input').first().json.party_size
  }
};
