const input = $input.item.json;

console.log('ğŸ” Input recibido:', JSON.stringify(input, null, 2));

// âœ… Validar datos mÃ­nimos
const required = ['reservation_date', 'reservation_time', 'party_size', 'restaurant_id'];
const missing = required.filter(field => !input[field]);

if (missing.length > 0) {
  throw new Error(`âŒ Faltan campos obligatorios: ${missing.join(', ')}`);
}

return {
  // âœ… DATOS OBLIGATORIOS
  restaurant_id: input.restaurant_id,
  customer_id: input.customer_id || null,
  customer_phone: input.customer_phone,
  customer_name: input.customer_name,

  // âœ… DATOS DE LA RESERVA
  reservation_date: input.reservation_date,
  reservation_time: input.reservation_time,
  party_size: parseInt(input.party_size),

  // âœ… DATOS OPCIONALES
  preferred_zone: input.preferred_zone || null,
  special_requests: input.special_requests || null,
  source: input.source || 'agent_whatsapp'
};

// =====================================================
// ğŸ”„ PROCESAR DISPONIBILIDAD
// =====================================================

const validacion = $('ğŸ” Validar Input').first().json;
const resultado = $input.first().json;

console.log('ğŸ“Š Respuesta RPC:', JSON.stringify(resultado, null, 2));

const disponibilidad = resultado;

if (!disponibilidad || typeof disponibilidad !== 'object') {
  console.log('âŒ No se recibiÃ³ respuesta de disponibilidad');
  return {
    disponible: false,
    mensaje: 'Error al verificar disponibilidad',
    ...validacion
  };
}

if (!disponibilidad.available) {
  console.log('âŒ No hay disponibilidad');
  return {
    disponible: false,
    mensaje: disponibilidad.message || 'No hay disponibilidad',
    ...validacion
  };
}

console.log('âœ… Disponibilidad encontrada. Tipo:', disponibilidad.type);

// ===== EXTRAER SLOT IDS Y TABLE IDS =====
let slot_ids = [];
let table_ids = [];

if (disponibilidad.type === 'single') {
  slot_ids = [disponibilidad.slot_id];
  table_ids = [disponibilidad.table_id]; // âœ… NUEVO
} else if (disponibilidad.type === 'combination') {
  slot_ids = disponibilidad.slot_ids || [];
  table_ids = disponibilidad.table_ids || []; // âœ… NUEVO
}

if (slot_ids.length === 0) {
  console.log('âŒ No se encontraron slot_ids vÃ¡lidos');
  return {
    disponible: false,
    mensaje: 'Error: No se pudieron identificar las mesas',
    ...validacion
  };
}

console.log('ğŸ¯ Slot IDs:', slot_ids);
console.log('ğŸ¯ Table IDs:', table_ids);

return {
  disponible: true,
  slot_ids: slot_ids,
  table_ids: table_ids, // âœ… NUEVO
  tipo: disponibilidad.type,
  tables: disponibilidad.tables || disponibilidad.table_names || [],
  zone: disponibilidad.zone,
  ...validacion
};

// =====================================================
// ğŸ“¦ PREPARAR DATOS PARA reservation_tables
// =====================================================

const reservationData = $input.first().json;
const disponibilidadData = $('ğŸ”„ Procesar Disponibilidad').first().json;

const reservation_id = reservationData.id;
const table_ids = disponibilidadData.table_ids;

console.log('ğŸ“¦ Reservation ID:', reservation_id);
console.log('ğŸ“¦ Table IDs:', table_ids);

// Crear un objeto por cada table_id
const items = table_ids.map(table_id => ({
  reservation_id: reservation_id,
  table_id: table_id,
  slot_ids: disponibilidadData.slot_ids, // Para el siguiente paso
  ...reservationData
}));

console.log('ğŸ“¦ Items para reservation_tables:', items.length);

return items;

// =====================================================
// ğŸ”„ AGRUPAR Y PREPARAR ACTUALIZACIÃ“N DE SLOTS
// =====================================================

const items = $input.all();

if (items.length === 0) {
  throw new Error('âŒ No hay items para procesar');
}

// Todos los items tienen el mismo reservation_id y slot_ids
const firstItem = items[0].json;

console.log('ğŸ”„ Items procesados:', items.length);
console.log('ğŸ”„ Reservation ID:', firstItem.reservation_id);
console.log('ğŸ”„ Slot IDs a actualizar:', firstItem.slot_ids);

return {
  reservation_id: firstItem.reservation_id,
  slot_ids: firstItem.slot_ids,
  customer_name: firstItem.customer_name,
  reservation_date: firstItem.reservation_date,
  reservation_time: firstItem.reservation_time,
  party_size: firstItem.party_size,
  zone: firstItem.zone,
  tables: firstItem.tables,
  num_tables: items.length
};

// =====================================================
// ğŸ“¦ PREPARAR ACTUALIZACIÃ“N DE SLOTS
// =====================================================

const data = $input.first().json;
const slot_ids = data.slot_ids;

console.log('ğŸ“¦ Preparando actualizaciÃ³n de', slot_ids.length, 'slots');

// Crear un item por cada slot_id
const items = slot_ids.map(slot_id => ({
  slot_id: slot_id,
  reservation_id: data.reservation_id
}));

return items;

// =====================================================
// âœ… RESPUESTA FINAL
// =====================================================

const items = $input.all();
const aggregatedData = $('ğŸ”„ Agrupar Resultados').first().json;

console.log('âœ… Slots actualizados:', items.length);

return {
  success: true,
  reservation_id: aggregatedData.reservation_id,
  customer_name: aggregatedData.customer_name,
  reservation_date: aggregatedData.reservation_date,
  reservation_time: aggregatedData.reservation_time,
  party_size: aggregatedData.party_size,
  tables: aggregatedData.tables,
  zone: aggregatedData.zone,
  num_tables: aggregatedData.num_tables,
  is_combined: aggregatedData.num_tables > 1,
  slots_reserved: items.length,
  message: `Reserva creada exitosamente para ${aggregatedData.party_size} personas el ${aggregatedData.reservation_date} a las ${aggregatedData.reservation_time} en ${aggregatedData.zone}`
};

const data = $input.first().json;

console.log('âŒ No hay disponibilidad:', data.mensaje);

return {
  success: false,
  message: data.mensaje || 'No hay disponibilidad para crear la reserva',
  details: {
    date: data.reservation_date,
    time: data.reservation_time,
    party_size: data.party_size,
    zone: data.preferred_zone
  }
};
