const input = $input.item.json;

console.log('ğŸ” Input recibido:', JSON.stringify(input, null, 2));

// âœ… Validar campos obligatorios
const required = ['reservation_id', 'restaurant_id'];
const missing = required.filter(field => !input[field]);

if (missing.length > 0) {
  throw new Error(`âŒ Faltan campos obligatorios: ${missing.join(', ')}`);
}

// Al menos uno de estos campos debe estar presente
if (!input.new_date && !input.new_time && !input.new_party_size && !input.special_requests) {
  throw new Error('âŒ Debes proporcionar al menos un campo a modificar');
}

return {
  reservation_id: input.reservation_id,
  restaurant_id: input.restaurant_id,

  // Nuevos valores (pueden ser null si no se modifican)
  new_date: input.new_date || null,
  new_time: input.new_time || null,
  new_party_size: input.new_party_size ? parseInt(input.new_party_size) : null,
  new_slot_id: input.new_slot_id || null,
  new_table_id: input.new_table_id || null,

  // Opcionales
  preferred_zone: input.preferred_zone || null,
  special_requests: input.special_requests || null
};

const inputData = $('ğŸ” Validar Input').item.json;
const reservation = $input.item.json;

if (!reservation || !reservation.id) {
  throw new Error('âŒ Reserva no encontrada');
}

console.log('ğŸ“‹ Reserva encontrada:', {
  id: reservation.id,
  current_date: reservation.reservation_date,
  current_time: reservation.reservation_time,
  current_party_size: reservation.party_size,
  table_id: reservation.table_id
});

// Validar que la reserva se puede modificar
const validStatuses = ['pending', 'confirmed', 'pending_approval'];
if (!validStatuses.includes(reservation.status)) {
  throw new Error(`âŒ No se puede modificar una reserva con estado: ${reservation.status}`);
}

// Determinar valores finales
const finalDate = inputData.new_date || reservation.reservation_date;
const finalTime = inputData.new_time || reservation.reservation_time;
const finalPartySize = inputData.new_party_size || reservation.party_size;
const finalTableId = inputData.new_table_id || reservation.table_id;

// âœ… Detectar si cambiÃ³ fecha/hora (necesitamos cambiar slots)
const dateTimeChanged = 
  (inputData.new_date && inputData.new_date !== reservation.reservation_date) ||
  (inputData.new_time && inputData.new_time !== reservation.reservation_time);

console.log('ğŸ”„ Cambios detectados:', {
  date_changed: inputData.new_date && inputData.new_date !== reservation.reservation_date,
  time_changed: inputData.new_time && inputData.new_time !== reservation.reservation_time,
  party_size_changed: inputData.new_party_size && inputData.new_party_size !== reservation.party_size,
  need_slot_change: dateTimeChanged
});

return {
  // Datos originales
  reservation_id: reservation.id,
  restaurant_id: reservation.restaurant_id,
  customer_id: reservation.customer_id,
  customer_phone: reservation.customer_phone,
  customer_name: reservation.customer_name,
  old_table_id: reservation.table_id,
  old_date: reservation.reservation_date,
  old_time: reservation.reservation_time,
  old_party_size: reservation.party_size,

  // Datos finales
  final_date: finalDate,
  final_time: finalTime,
  final_party_size: finalPartySize,
  final_table_id: finalTableId,
  special_requests: inputData.special_requests || reservation.special_requests,

  // Nuevos IDs (del check_availability previo)
  new_slot_id: inputData.new_slot_id,
  new_table_id: inputData.new_table_id,

  // Flag
  need_slot_change: dateTimeChanged
};

const reservation = $input.item.json;
const oldData = $('ğŸ”„ Preparar ModificaciÃ³n').item.json;

console.log('âœ… Reserva modificada exitosamente:', reservation.id);

const changes = [];
if (oldData.old_date !== oldData.final_date) {
  changes.push(`fecha: ${oldData.old_date} â†’ ${oldData.final_date}`);
}
if (oldData.old_time !== oldData.final_time) {
  changes.push(`hora: ${oldData.old_time} â†’ ${oldData.final_time}`);
}
if (oldData.old_party_size !== oldData.final_party_size) {
  changes.push(`personas: ${oldData.old_party_size} â†’ ${oldData.final_party_size}`);
}

return {
  success: true,
  reservation_id: reservation.id,
  message: `Â¡Listo, ${oldData.customer_name}! Tu reserva ha sido modificada exitosamente. Nueva fecha: ${oldData.final_date} a las ${oldData.final_time} para ${oldData.final_party_size} personas.`,
  details: {
    reservation_id: reservation.id,
    changes: changes.join(', '),
    new_date: oldData.final_date,
    new_time: oldData.final_time,
    new_party_size: oldData.final_party_size,
    customer_name: oldData.customer_name
  }
};
