const input = $input.first().json;

console.log('ğŸ“¥ Input recibido:', input);

const fecha = input.date || input.reservation_date || '';
const hora = input.time || input.reservation_time || '';
const personas = parseInt(input.party_size || input.people || 0);
const ubicacion = input.reservation_location || input.location || null;
const restaurant_id = input.restaurant_id || '';

if (!fecha || !/^\d{4}-\d{2}-\d{2}$/.test(fecha)) {
  throw new Error('Formato de fecha invÃ¡lido. Se requiere YYYY-MM-DD');
}

const hoy = new Date().toISOString().split('T')[0];
if (fecha < hoy) {
  throw new Error('No se puede consultar disponibilidad en fechas pasadas');
}

if (!hora || !/^\d{2}:\d{2}(:\d{2})?$/.test(hora)) {
  throw new Error('Formato de hora invÃ¡lido. Se requiere HH:MM');
}

const horaNormalizada = hora.substring(0, 5);

if (!personas || personas < 1 || personas > 20) {
  throw new Error('NÃºmero de personas debe estar entre 1 y 20');
}

if (!restaurant_id) {
  throw new Error('restaurant_id es obligatorio');
}

console.log('âœ… Validado:', { fecha, hora: horaNormalizada, personas, restaurant_id });

return {
  fecha,
  hora: horaNormalizada,
  personas,
  ubicacion,
  restaurant_id,
  fecha_formateada: new Date(fecha).toLocaleDateString('es-ES', { 
    weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' 
  })
};

const slots = $input.all();
const validacion = $('ğŸ” Validar Input').first().json;

console.log('ğŸ“Š Slots encontrados:', slots.length);

if (slots.length === 0) {
  return { tiene_slots: false };
}

// Filtrar por table_id para obtener info de mesas
const tableIds = [...new Set(slots.map(s => s.json.table_id))];

console.log('ğŸ”‘ Table IDs:', tableIds);

return {
  tiene_slots: true,
  table_ids: tableIds,
  total_slots: slots.length,
  ...validacion
};

const procesar = $('ğŸ”¢ Procesar Slots').first().json;
const mesas = $input.all();

const tableIds = procesar.table_ids || [];
const personas = procesar.personas;

// Filtrar mesas disponibles con capacidad suficiente
const mesasValidas = mesas
  .map(m => m.json)
  .filter(mesa => 
    tableIds.includes(mesa.id) && 
    mesa.capacity >= personas
  )
  .sort((a, b) => a.capacity - b.capacity);

console.log('âœ… Mesas vÃ¡lidas:', mesasValidas.length);

if (mesasValidas.length === 0) {
  return {
    disponible: false,
    mensaje: `Lo siento, no tenemos mesas con capacidad suficiente para ${personas} personas.`,
    alternativas: [],
    detalles: {
      fecha: procesar.fecha,
      hora: procesar.hora,
      personas: personas
    },
    accion_sugerida: 'buscar_otra_hora'
  };
}

const mejorMesa = mesasValidas[0];

const mensaje = `Â¡Perfecto! SÃ­ tenemos disponibilidad para ${personas} persona${personas > 1 ? 's' : ''} el ${procesar.fecha_formateada} a las ${procesar.hora}. Tenemos ${mesasValidas.length} mesa${mesasValidas.length > 1 ? 's' : ''} disponible${mesasValidas.length > 1 ? 's' : ''}.`;

return {
  disponible: true,
  mensaje: mensaje,
  detalles: {
    fecha: procesar.fecha,
    hora: procesar.hora,
    personas: personas,
    mesas_disponibles: mesasValidas.length,
    mejor_opcion: {
      mesa: mejorMesa.table_number,
      capacidad: mejorMesa.capacity,
      zona: mejorMesa.zone || 'General'
    }
  },
  accion_sugerida: 'proceder_con_reserva'
};

const validacion = $('ğŸ” Validar Input').first().json;
const alternativas = $input.all();

if (alternativas.length === 0) {
  return {
    tiene_alternativas: false,
    mensaje: `Lo siento, no tenemos disponibilidad para ${validacion.personas} personas el ${validacion.fecha_formateada}. Â¿Te gustarÃ­a que busque en otra fecha?`,
    disponible: false,
    alternativas: [],
    detalles: {
      fecha_solicitada: validacion.fecha,
      hora_solicitada: validacion.hora,
      personas: validacion.personas
    },
    accion_sugerida: 'sugerir_otra_fecha'
  };
}

// Agrupar por hora
const horasPorSlot = {};
alternativas.forEach(slot => {
  const hora = slot.json.start_time;
  if (!horasPorSlot[hora]) {
    horasPorSlot[hora] = [];
  }
  horasPorSlot[hora].push(slot.json.table_id);
});

const horasDisponibles = Object.keys(horasPorSlot)
  .sort()
  .slice(0, 5);

const listaHoras = horasDisponibles.join(', ');
const mensaje = `Lo siento, no tenemos disponibilidad a las ${validacion.hora} para ${validacion.personas} personas. Sin embargo, sÃ­ tenemos disponibilidad en estos otros horarios el ${validacion.fecha_formateada}: ${listaHoras}. Â¿Alguno de estos horarios te vendrÃ­a bien?`;

return {
  tiene_alternativas: true,
  disponible: false,
  mensaje: mensaje,
  alternativas: horasDisponibles,
  detalles: {
    fecha: validacion.fecha,
    hora_solicitada: validacion.hora,
    personas: validacion.personas,
    total_alternativas: horasDisponibles.length
  },
  accion_sugerida: 'elegir_alternativa'
};
