// =====================================================
// ğŸ” VALIDACIÃ“N DE INPUT CON SOPORTE DE ZONAS
// =====================================================

const input = $input.first().json;

console.log('ğŸ“¥ Input recibido:', input);

// ===== VALIDAR FECHA =====
const fecha = input.date || input.reservation_date || '';

if (!fecha || !/^\d{4}-\d{2}-\d{2}$/.test(fecha)) {
  throw new Error('Formato de fecha invÃ¡lido. Se requiere YYYY-MM-DD');
}

const hoy = new Date().toISOString().split('T')[0];
if (fecha < hoy) {
  throw new Error('No se puede consultar disponibilidad en fechas pasadas');
}

// ===== VALIDAR HORA =====
const hora = input.time || input.reservation_time || '';

if (!hora || !/^\d{2}:\d{2}(:\d{2})?$/.test(hora)) {
  throw new Error('Formato de hora invÃ¡lido. Se requiere HH:MM');
}

const horaNormalizada = hora.substring(0, 5);

// ===== VALIDAR PERSONAS =====
const personas = parseInt(input.party_size || input.people || 0);

if (!personas || personas < 1 || personas > 20) {
  throw new Error('NÃºmero de personas debe estar entre 1 y 20');
}

// ===== VALIDAR RESTAURANT_ID =====
const restaurant_id = input.restaurant_id || '';

if (!restaurant_id) {
  throw new Error('restaurant_id es obligatorio');
}

// âœ… NUEVO: VALIDAR Y NORMALIZAR ZONA
const zonaRaw = input.preferred_zone || input.zone || input.reservation_location || input.location || null;
const validZones = ['interior', 'terraza', 'barra', 'privado', 'any'];
let zona = null;

if (zonaRaw) {
  const zonaNormalizada = zonaRaw.toLowerCase().trim();
  if (validZones.includes(zonaNormalizada)) {
    zona = zonaNormalizada === 'any' ? null : zonaNormalizada;
    console.log(`âœ… Zona vÃ¡lida: ${zona || 'todas'}`);
  } else {
    console.log(`âš ï¸ Zona invÃ¡lida: "${zonaRaw}". Se buscarÃ¡ en todas las zonas.`);
    zona = null;  // Fallback: buscar en todas las zonas
  }
}

console.log('âœ… Validado:', { fecha, hora: horaNormalizada, personas, zona: zona || 'todas', restaurant_id });

// ===== RETURN =====
return {
  fecha,
  hora: horaNormalizada,
  personas,
  zona,  // null = buscar en todas las zonas
  restaurant_id,
  fecha_formateada: new Date(fecha).toLocaleDateString('es-ES', { 
    weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' 
  })
};

// =====================================================
// ğŸ”¢ PROCESAR SLOTS CON FILTRO DE ZONA Y CAPACIDAD
// =====================================================

const slots = $input.all();
const validacion = $('ğŸ” Validar Input').first().json;

console.log('ğŸ“Š Slots encontrados:', slots.length);

if (slots.length === 0) {
  return { 
    tiene_slots: false,
    ...validacion
  };
}

// âœ… NUEVO: Filtrar por zona SI se especificÃ³
let slotsFiltrados = slots;

if (validacion.zona) {
  slotsFiltrados = slots.filter(s => s.json.zone === validacion.zona);
  console.log(`ğŸ” Slots en zona "${validacion.zona}": ${slotsFiltrados.length}`);
}

if (slotsFiltrados.length === 0) {
  return { 
    tiene_slots: false,
    zona_solicitada: validacion.zona,
    zona_no_disponible: true,
    slots_totales: slots.length,  // Hay slots pero en otras zonas
    ...validacion
  };
}

// Agrupar por table_id para contar slots por mesa
const slotsPorMesa = {};
slotsFiltrados.forEach(slot => {
  const tableId = slot.json.table_id;
  if (!slotsPorMesa[tableId]) {
    slotsPorMesa[tableId] = {
      slots: [],
      zone: slot.json.zone
    };
  }
  slotsPorMesa[tableId].slots.push(slot.json);
});

const tableIds = Object.keys(slotsPorMesa);

console.log(`ğŸ”‘ Mesas con slots: ${tableIds.length}`);

return {
  tiene_slots: true,
  table_ids: tableIds,
  slots_por_mesa: slotsPorMesa,
  total_slots: slotsFiltrados.length,
  zona_solicitada: validacion.zona,
  ...validacion
};

// =====================================================
// âœ… RESPUESTA: SÃ HAY DISPONIBILIDAD
// =====================================================

const procesar = $('ğŸ”¢ Procesar Slots').first().json;
const mesas = $input.all();

const tableIds = procesar.table_ids || [];
const personas = procesar.personas;
const zona = procesar.zona;

// Filtrar mesas disponibles con capacidad suficiente
const mesasValidas = mesas
  .map(m => m.json)
  .filter(mesa => 
    tableIds.includes(mesa.id) && 
    mesa.capacity >= personas
  )
  .sort((a, b) => a.capacity - b.capacity);

console.log('âœ… Mesas vÃ¡lidas:', mesasValidas.length);

if (mesasValidas.length === 0) {
  // âœ… MEJORADO: Mensaje diferente si se especificÃ³ zona
  const mensajeBase = `Lo siento, no tenemos mesas con capacidad suficiente para ${personas} personas`;
  const mensajeZona = zona ? ` en la zona ${zona}` : '';
  const sugerencia = zona ? ' Â¿Te irÃ­a bien en otra zona?' : '';

  return {
    disponible: false,
    mensaje: `${mensajeBase}${mensajeZona}.${sugerencia}`,
    alternativas: [],
    detalles: {
      fecha: procesar.fecha,
      hora: procesar.hora,
      personas: personas,
      zona_solicitada: zona
    },
    accion_sugerida: zona ? 'sugerir_otra_zona' : 'buscar_otra_hora'
  };
}

const mejorMesa = mesasValidas[0];

// âœ… MEJORADO: Mencionar zona solo si se especificÃ³
const mensajeZona = zona ? ` en ${zona}` : '';
const mensaje = `Â¡Perfecto! SÃ­ tenemos disponibilidad${mensajeZona} para ${personas} persona${personas > 1 ? 's' : ''} el ${procesar.fecha_formateada} a las ${procesar.hora}. Tenemos ${mesasValidas.length} mesa${mesasValidas.length > 1 ? 's' : ''} disponible${mesasValidas.length > 1 ? 's' : ''}.`;

return {
  disponible: true,
  mensaje: mensaje,
  detalles: {
    fecha: procesar.fecha,
    hora: procesar.hora,
    personas: personas,
    zona_solicitada: zona,  // âœ… NUEVO
    mesas_disponibles: mesasValidas.length,
    mejor_opcion: {
      mesa: mejorMesa.table_number,
      capacidad: mejorMesa.capacity,
      zona: mejorMesa.zone || 'interior'
    }
  },
  accion_sugerida: 'proceder_con_reserva'
};

// =====================================================
// âš ï¸ RESPUESTA: NO HAY DISPONIBILIDAD (CON ALTERNATIVAS)
// =====================================================

const validacion = $('ğŸ” Validar Input').first().json;
const procesar = $('ğŸ”¢ Procesar Slots').first().json;
const alternativas = $input.all();

const zona = validacion.zona;
const personas = validacion.personas;

// ===== CASO 1: NO HAY ALTERNATIVAS EN TODO EL DÃA =====
if (alternativas.length === 0) {
  return {
    tiene_alternativas: false,
    mensaje: `Lo siento, no tenemos disponibilidad para ${personas} personas el ${validacion.fecha_formateada}. Â¿Te gustarÃ­a que busque en otra fecha?`,
    disponible: false,
    alternativas: [],
    detalles: {
      fecha_solicitada: validacion.fecha,
      hora_solicitada: validacion.hora,
      personas: personas,
      zona_solicitada: zona
    },
    accion_sugerida: 'sugerir_otra_fecha'
  };
}

// ===== CASO 2: HAY SLOTS PERO EN OTRA ZONA =====
if (procesar.zona_no_disponible && zona) {
  // Buscar en quÃ© zonas SÃ hay disponibilidad
  const zonasDisponibles = [...new Set(
    alternativas
      .map(s => s.json.zone)
      .filter(z => z && z !== zona)
  )];

  if (zonasDisponibles.length > 0) {
    const listaZonas = zonasDisponibles.join(', ');
    return {
      tiene_alternativas: true,
      disponible: false,
      mensaje: `Lo siento, no tenemos disponibilidad en ${zona} a las ${validacion.hora} para ${personas} personas. Sin embargo, sÃ­ tenemos disponibilidad en: ${listaZonas}. Â¿Te irÃ­a bien alguna de estas zonas?`,
      alternativas_zona: zonasDisponibles,
      detalles: {
        fecha: validacion.fecha,
        hora_solicitada: validacion.hora,
        personas: personas,
        zona_solicitada: zona,
        zonas_alternativas: zonasDisponibles
      },
      accion_sugerida: 'elegir_otra_zona'
    };
  }
}

// ===== CASO 3: HAY SLOTS EN OTRAS HORAS =====
// Agrupar por hora
const horasPorSlot = {};
alternativas.forEach(slot => {
  const hora = slot.json.start_time;
  const zonaSlot = slot.json.zone;

  // Si se especificÃ³ zona, solo contar esa zona
  if (zona && zonaSlot !== zona) {
    return;
  }

  if (!horasPorSlot[hora]) {
    horasPorSlot[hora] = {
      slots: 0,
      tables: new Set()
    };
  }
  horasPorSlot[hora].slots++;
  horasPorSlot[hora].tables.add(slot.json.table_id);
});

const horasDisponibles = Object.keys(horasPorSlot)
  .filter(h => h !== validacion.hora)  // Excluir hora solicitada
  .sort()
  .slice(0, 5);

if (horasDisponibles.length === 0) {
  // No hay alternativas en la zona solicitada
  const mensajeZona = zona ? ` en ${zona}` : '';
  return {
    tiene_alternativas: false,
    disponible: false,
    mensaje: `Lo siento, no tenemos disponibilidad${mensajeZona} para ${personas} personas el ${validacion.fecha_formateada}. Â¿Te gustarÃ­a que busque en otra fecha?`,
    alternativas: [],
    detalles: {
      fecha: validacion.fecha,
      hora_solicitada: validacion.hora,
      personas: personas,
      zona_solicitada: zona
    },
    accion_sugerida: 'sugerir_otra_fecha'
  };
}

const listaHoras = horasDisponibles.join(', ');
const mensajeZona = zona ? ` en ${zona}` : '';
const mensaje = `Lo siento, no tenemos disponibilidad a las ${validacion.hora}${mensajeZona} para ${personas} personas. Sin embargo, sÃ­ tenemos disponibilidad en estos otros horarios el ${validacion.fecha_formateada}: ${listaHoras}. Â¿Alguno de estos horarios te vendrÃ­a bien?`;

return {
  tiene_alternativas: true,
  disponible: false,
  mensaje: mensaje,
  alternativas_horario: horasDisponibles,
  detalles: {
    fecha: validacion.fecha,
    hora_solicitada: validacion.hora,
    personas: personas,
    zona_solicitada: zona,
    total_alternativas: horasDisponibles.length
  },
  accion_sugerida: 'elegir_alternativa'
};
