const fusionarClientesData = $('沛ｪ Buscar Restaurante').first().json;
const customerData = $('汨､ Buscar Cliente').first().json || $input.first().json;
const reservasData = $('沒 Get Reservas Activas').first().json;

console.log('洫ｹ Limpiando y estructurando datos para el prompt...');

// Extraer informaciﾃｳn clave del restaurante
const restaurante = {
  id: fusionarClientesData.id || fusionarClientesData.restaurant_id,
  nombre: fusionarClientesData.name || fusionarClientesData.restaurant_name,
  telefono: fusionarClientesData.phone,
  email: fusionarClientesData.email,
  direccion: fusionarClientesData.address,
  ciudad: fusionarClientesData.city,
  tipoCocina: fusionarClientesData.cuisine_type
};

// Extraer informaciﾃｳn del cliente
const cliente = {
  id: customerData.id,
  nombre: customerData.name,
  email: customerData.email,
  telefono: customerData.phone
};

// Procesar reservas existentes
const reservasExistentes = reservasData.items || [];

// Estructurar informaciﾃｳn limpia para el prompt
const informacionLimpia = {
  restaurante: restaurante,
  cliente: cliente,
  reservasExistentes: reservasExistentes.length,
  detalleReservas: reservasExistentes.map(r => ({
    fecha: r.reservation_date,
    hora: r.reservation_time,
    personas: r.party_size,
    estado: r.status,
    zona: r.preferred_zone || 'no especificada',
    notas: r.special_requests || 'ninguna'
  })),
  contexto: {
    hasReservas: reservasExistentes.length > 0,
    ultimaReserva: reservasExistentes.length > 0 ? {
      fecha: reservasExistentes[reservasExistentes.length - 1].reservation_date,
      estado: reservasExistentes[reservasExistentes.length - 1].status
    } : null
  }
};

return informacionLimpia;

